var express		 =	require("express"),
 	app			 =	express(),
	mongoose	 =	require("mongoose"),
	bodyParser	 =	require("body-parser"),
	passport	 =  require("passport"),
	localStrategy=	require("passport-local");
	Campground	 =	require("./models/campground"),
	Comment		 =	require("./models/comment"),
	User		 =	require("./models/user"),	
	seedDB		 =	require("./seeds.js");

var campgroundsRoutes = require("./routes/campgrounds");
var commentsRoutes = require("./routes/comments");
var indexRoutes = require("./routes/index");

app.set("view engine", "ejs");
app.use(bodyParser.urlencoded({extended : true}));
app.use(express.static(__dirname +"/public"));


//PASSPORT CONFIGURATION
app.use(require("express-session")({
	secret: "Abhinab is a developer",
	resave : false,
	saveUninitialized : false
}));

app.use(passport.initialize());
app.use(passport.session());
passport.use(new localStrategy(User.authenticate()));
passport.serializeUser(User.serializeUser());
passport.deserializeUser(User.deserializeUser());

mongoose.set('useNewUrlParser', true);
mongoose.set('useUnifiedTopology', true);
mongoose.connect("mongodb://localhost/yelp_camp");
seedDB();



var campgrounds=[
			{name:"Boys of Scout Campground", image:"data:image/jpeg;base64,"},
			{name:"Maithon Dam", image:"data:image/jpeg;base64,"},
			{name:"Pamchet Dam", image:"https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQD-iDNoYmXXgr3ldx0YJ4oQWjOMB4Goyj2u8uY6No4FO404tDf&usqp=CAU"},
			{name:"Pamchet Dam", image:"https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQD-iDNoYmXXgr3ldx0YJ4oQWjOMB4Goyj2u8uY6No4FO404tDf&usqp=CAU"},
			{name:"Pamchet Dam", image:"https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQD-iDNoYmXXgr3ldx0YJ4oQWjOMB4Goyj2u8uY6No4FO404tDf&usqp=CAU"},
			{name:"Pamchet Dam", image:"https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQD-iDNoYmXXgr3ldx0YJ4oQWjOMB4Goyj2u8uY6No4FO404tDf&usqp=CAU"},
			{name:"Pamchet Dam", image:"https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQD-iDNoYmXXgr3ldx0YJ4oQWjOMB4Goyj2u8uY6No4FO404tDf&usqp=CAU"}
];

app.use(function(req,res,next){
	res.locals.loggedUser = req.user; 
	next();
 });

app.use(indexRoutes);
app.use("/campgrounds/:id/comments",commentsRoutes);
app.use("/campgrounds",campgroundsRoutes);





app.listen(process.env.PORT || 3000,process.env.IP,function(){
	console.log("The Yelpcamp server has started!");
});